"""Pool implementation abstract factory, and alias definitions."""

# Import from kombu directly as it's used
# early in the import stage, where celeryapp.utils loads
# too much (e.g., for eventlet patching)
from kombu.utils.imports import symbol_by_name

__all__ = ('get_implementation', 'get_available_pool_names',)

ALIASES = {
    'prefork': 'celeryapp.concurrency.prefork:TaskPool',
    'eventlet': 'celeryapp.concurrency.eventlet:TaskPool',
    'gevent': 'celeryapp.concurrency.gevent:TaskPool',
    'solo': 'celeryapp.concurrency.solo:TaskPool',
    'processes': 'celeryapp.concurrency.prefork:TaskPool',  # XXX compat alias
}

try:
    import concurrent.futures  # noqa: F401
except ImportError:
    pass
else:
    ALIASES['threads'] = 'celeryapp.concurrency.thread:TaskPool'


def get_implementation(cls):
    """Return pool implementation by name."""
    return symbol_by_name(cls, ALIASES)


def get_available_pool_names():
    """Return all available pool type names."""
    return tuple(ALIASES.keys())
